func TestLoadProofsResign(t *testing.T) {
	err := os.RemoveAll("./output/")
	require.NoError(t, err)
	err = logging.SetGlobalLogger("info", "capital", "console", nil)
	require.NoError(t, err)
	version := "test.version"

	// Open ethereum keystore
	jsonBytes, err := os.ReadFile("../examples/initiator/UTC--2024-06-14T14-05-12.366668334Z--dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9")
	require.NoError(t, err)
	keyStorePassword, err := os.ReadFile(filepath.Clean("../examples/initiator/password"))
	require.NoError(t, err)
	sk, err := keystore.DecryptKey(jsonBytes, string(keyStorePassword))
	require.NoError(t, err)
	owner := eth_crypto.PubkeyToAddress(sk.PrivateKey.PublicKey)

	stubClient := &stubs.Client{
		CallContractF: func(call ethereum.CallMsg) ([]byte, error) {
			return nil, nil
		},
	}
	servers, ops := createOperatorsFromExamplesFolder(t, version, stubClient)
	operators, err := json.Marshal(ops)
	require.NoError(t, err)
	RootCmd := &cobra.Command{
		Use:   "ssv-dkg",
		Short: "CLI for running Distributed Key Generation protocol",
		PersistentPreRun: func(cmd *cobra.Command, args []string) {
		},
	}
	RootCmd.AddCommand(cli_initiator.StartResigning)
	RootCmd.Short = "ssv-dkg-test"
	RootCmd.Version = version
	cli_initiator.StartResigning.Version = version
	t.Run("load from file", func(t *testing.T) {
		args := []string{"resign",
			"--proofsFilePath", "./stubs/4/000001-0xaa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39/proofs.json",
			"--operatorsInfo", string(operators),
			"--owner", owner.Hex(),
			"--withdrawAddress", "0x81592c3de184a3e2c0dcb5a261bc107bfa91f494",
			"--operatorIDs", "11,22,33,44",
			"--nonce", "10",
			"--ethKeystorePath", "./stubs/UTC--2024-06-14T14-05-12.366668334Z--dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9",
			"--ethKeystorePass", "./stubs/password"}
		RootCmd.SetArgs(args)
		err = RootCmd.Execute()
		require.NoError(t, err)
		resetFlags(RootCmd)
	})
	t.Run("load from raw json", func(t *testing.T) {
		proofsRawJSON := `[{"proof":{"validator":"aa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39","encrypted_share":"","share_pub":"8ce17b6af1fc18aa66298ef3f722cfa3e891d7cb5bdf116051a0af5aeb6f6c38a7860cd3d47427fb584240706c3a4acb","owner":"dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9"},"signature":""},{"proof":{"validator":"aa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39","encrypted_share":"","share_pub":"8f476e4cb557aede4137696d63b2910a88bc55123e9a8a6b03cbc338f8fec3bf8bfffaf6dea5607b76c7d00915b4dd25","owner":"dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9"},"signature":""},{"proof":{"validator":"aa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39","encrypted_share":"","share_pub":"a99bf34e0aef11749080ed4cf36acf1711460f5bdbd098b28de1afac618cc273f457613ca454262d8f5e46a8d010dd00","owner":"dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9"},"signature":""},{"proof":{"validator":"aa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39","encrypted_share":"","share_pub":"84faff90116da6fc1222fc2060ea24cae6f135c5d2684b09e5f9b0253d682df0a96025e81a606aafb2d434d774ec9340","owner":"dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9"},"signature":""}]`
		args := []string{"resign",
			"--proofsRawJSON", proofsRawJSON,
			"--operatorsInfo", string(operators),
			"--owner", owner.Hex(),
			"--withdrawAddress", "0x81592c3de184a3e2c0dcb5a261bc107bfa91f494",
			"--operatorIDs", "11,22,33,44",
			"--nonce", "10",
			"--ethKeystorePath", "./stubs/UTC--2024-06-14T14-05-12.366668334Z--dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9",
			"--ethKeystorePass", "./stubs/password"}
		RootCmd.SetArgs(args)
		err = RootCmd.Execute()
		require.NoError(t, err)
		resetFlags(RootCmd)
	})
	t.Run("error - both flags proofsRawJSON and proofsFilePath", func(t *testing.T) {
		proofsRawJSON := `[{"proof":{"validator":"aa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39","encrypted_share":"","share_pub":"8ce17b6af1fc18aa66298ef3f722cfa3e891d7cb5bdf116051a0af5aeb6f6c38a7860cd3d47427fb584240706c3a4acb","owner":"dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9"},"signature":""},{"proof":{"validator":"aa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39","encrypted_share":"","share_pub":"8f476e4cb557aede4137696d63b2910a88bc55123e9a8a6b03cbc338f8fec3bf8bfffaf6dea5607b76c7d00915b4dd25","owner":"dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9"},"signature":""},{"proof":{"validator":"aa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39","encrypted_share":"","share_pub":"a99bf34e0aef11749080ed4cf36acf1711460f5bdbd098b28de1afac618cc273f457613ca454262d8f5e46a8d010dd00","owner":"dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9"},"signature":""},{"proof":{"validator":"aa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39","encrypted_share":"","share_pub":"84faff90116da6fc1222fc2060ea24cae6f135c5d2684b09e5f9b0253d682df0a96025e81a606aafb2d434d774ec9340","owner":"dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9"},"signature":""}]`
		args := []string{"resign",
			"--proofsRawJSON", proofsRawJSON,
			"--proofsFilePath", "./stubs/4/000001-0xaa57eab07f1a740672d0c106867d366c798d3b932d373c88cf047da1a3c16d0816ac58bab5a9d6f6f4b63a07608f8f39/proofs.json",
			"--operatorsInfo", string(operators),
			"--owner", owner.Hex(),
			"--withdrawAddress", "0x81592c3de184a3e2c0dcb5a261bc107bfa91f494",
			"--operatorIDs", "11,22,33,44",
			"--nonce", "10",
			"--ethKeystorePath", "./stubs/UTC--2024-06-14T14-05-12.366668334Z--dcc846fa10c7cfce9e6eb37e06ed93b666cfc5e9",
			"--ethKeystorePass", "./stubs/password"}
		RootCmd.SetArgs(args)
		err = RootCmd.Execute()
		require.ErrorContains(t, err, "please provide either proofsRaw flag or proofsFilePath, not both")
	})
	err = os.RemoveAll("./output/")
	require.NoError(t, err)
	for _, srv := range servers {
		srv.HttpSrv.Close()
	}
}

